Point=Point=function(e,t){this.x=e||0,this.y=t||0},Point.prototype.getLength=function(){return Math.sqrt(this.x*this.x+this.y*this.y)},Point.prototype.add=function(e){return new Point(this.x+e.x,this.y+e.y)},Point.prototype.clone=function(){return new Point(this.x,this.y)},Point.prototype.distance=function(e,t){var n=Math.abs(t.x-e.x),r=Math.abs(t.y-e.y);return Math.sqrt(n*n+r*r)},Point.prototype.equals=function(e){return this.x===e.x&&this.y===e.y},Point.prototype.interpolate=function(e,t,n){var r=e.x+(t.x-e.x)*n,i=e.y+(r-e.x)*((t.y-e.y)/(t.x-e.x));return new Point(r,i)},Point.prototype.normalize=function(e){var t=(this.getLength()-e)/this.getLength();return new Point(this.x-this.x*t,this.y-this.y*t)},Point.prototype.offset=function(e,t){this.x=this.x+e,this.y=this.y+t},Point.prototype.polar=function(e,t){var n=e*Math.cos(t),r=e*Math.sin(t);return new Point(n,r)},Point.prototype.subtract=function(e){return new Point(this.x-e.x,this.y-e.y)},Point.prototype.toString=function(){return"{x:"+this.x+", y:"+this.y+", length:"+this.getLength()+"}"};